// App.js
import React, { useState, useEffect, useRef } from "react";
import "./styles.css";
import { FaSun, FaMoon } from "react-icons/fa";

function FadeInSection({ children }) {
  const [isVisible, setVisible] = useState(false);
  const domRef = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => setVisible(entry.isIntersecting));
    });
    observer.observe(domRef.current);
  }, []);

  return (
    <div className={`fade-in-section ${isVisible ? "is-visible" : ""}`} ref={domRef}>
      {children}
    </div>
  );
}

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle("dark-mode");
  };

  return (
    <div className={`App ${darkMode ? "dark" : "light"}`}>
      <button className="theme-toggle" onClick={toggleDarkMode}>
        {darkMode ? <FaMoon /> : <FaSun />}
      </button>

      <header className="title" style={{ fontFamily: "Nixie Tube Font" }}>
        1.048596
      </header>

      <FadeInSection>
        <section className="world-line">
          <h2>Projects</h2>
          <p>AWS security infrastructure, SIEM lab, Python-based financial analysis...</p>
        </section>
      </FadeInSection>

      <FadeInSection>
        <section className="world-line">
          <h2>Work Experience</h2>
          <p>IT Network & Cybersecurity Intern (TDS Telecom), IT Student (Great Oaks Cleaning Solution)...</p>
        </section>
      </FadeInSection>

      <FadeInSection>
        <section className="world-line singularity">
          <h2>The Singularity</h2>
          <p>End of the timeline, your professional Big Bang!</p>
        </section>
      </FadeInSection>
    </div>
  );
}

export default App;
