import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import FadeInSection from './FadeInSection';
import ThemeToggle from './ThemeToggle';
import Portfolio from './Portfolio';
import './App.css';

const lightTheme = {
  background: '#ffffff',
  text: '#0000ff',
  worldLine: '#0000ff',
};

const darkTheme = {
  background: '#000000',
  text: '#ff0000',
  worldLine: '#ff0000',
};

const AppContainer = styled.div`
  background-color: ${props => props.theme.background};
  color: ${props => props.theme.text};
  min-height: 100vh;
  padding: 20px;
`;

const WorldLine = styled.div`
  position: relative;
  margin: 20px 0;
  &:before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    width: 2px;
    height: 100%;
    background-color: ${props => props.theme.worldLine};
  }
`;

function App() {
  const [theme, setTheme] = useState('light');

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
      <AppContainer>
        <ThemeToggle theme={theme} toggleTheme={toggleTheme} />
        <h1 style={{ fontFamily: 'Nixie One, sans-serif', textAlign: 'center' }}>Thushal Bharadwaj Nelamane Venkatesh</h1>
        <WorldLine>
          <FadeInSection>
            <div>
              <h2>Education</h2>
              <p>Bachelor of Science in Information Technology - Information Security Concentration</p>
              <p>George Mason University, Fairfax, VA | May 2023</p>
            </div>
          </FadeInSection>
          <FadeInSection>
            <div>
              <h2>Professional Experience</h2>
              <p>IT Network and Cybersecurity Intern | TDS Telecom (Remote) | May 2023 - May 2024</p>
            </div>
          </FadeInSection>
          <FadeInSection>
            <div>
              <h2>Personal Projects</h2>
              <p>Cloud & Security Infrastructure</p>
            </div>
          </FadeInSection>
        </WorldLine>
        <Portfolio />
      </AppContainer>
    </ThemeProvider>
  );
}

export default App;
