import React, { useState } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import FadeInSection from './FadeInSection';
import ThemeToggle from './ThemeToggle';
import './App.css';

// Define your themes
const lightTheme = {
  background: '#ffffff',
  text: '#40E0D0', // Turquoise for light mode
  worldLine: '#40E0D0', // Turquoise for light mode
  linkGlow: '#40E0D0', // Turquoise for light mode
};

const darkTheme = {
  background: '#000000',
  text: '#ff0000', // Red for dark mode
  worldLine: '#ff0000', // Red for dark mode
  linkGlow: '#ff0000', // Red for dark mode
};

// Styled components
const AppContainer = styled.div`
  background-color: ${props => props.theme.background};
  color: ${props => props.theme.text};
  min-height: 100vh;
  padding: 20px;
  position: relative;
`;

const WorldLine = styled.div`
  position: absolute;
  left: 50%;
  top: 200px; /* Start below Section 1 */
  width: 2px;
  height: calc(100% - 200px); /* Adjust height to start from Section 1 */
  background-color: ${props => props.theme.worldLine};
  z-index: 0;
`;

const Section = styled.div`
  margin: 50px 0;
  padding: 20px;
  position: relative;
  z-index: 1;
  max-width: 45%;
  background-color: ${props => props.theme.background};
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);

  /* Alternating left and right alignment */
  &:nth-child(odd) {
    margin-left: 5%; /* Left-aligned */
  }

  &:nth-child(even) {
    margin-left: 50%; /* Right-aligned */
  }
`;

const CenterSection = styled.div`
  text-align: center;
  margin: 50px auto;
  padding: 20px;
  max-width: 600px;
  position: relative;
  z-index: 1;
  background-color: ${props => props.theme.background};
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const GlowLink = styled.a`
  color: ${props => props.theme.linkGlow};
  text-decoration: none;
