import React, { useState, useEffect, useRef } from "react";
import "./styles.css";
import { FaSun, FaMoon } from "react-icons/fa";
import Portfolio from './Portfolio'; // Import the Portfolio component

// FadeInSection component for fade-in effect
function FadeInSection({ children }) {
  const [isVisible, setVisible] = useState(false);
  const domRef = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => setVisible(entry.isIntersecting));
    });
    observer.observe(domRef.current);

    // Clean up the observer when the component unmounts
    return () => {
      observer.disconnect();
    };
  }, []);

  return (
    <div className={`fade-in-section ${isVisible ? "is-visible" : ""}`} ref={domRef}>
      {children}
    </div>
  );
}

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle("dark-mode");
  };

  return (
    <div className={`App ${darkMode ? "dark" : "light"}`}>
      <button className="theme-toggle" onClick={toggleDarkMode}>
        {darkMode ? <FaMoon /> : <FaSun />}
      </button>

      <header className="title">
        <h1 className="nixie">Thushal Bharadwaj Nelamane Venkatesh</h1>
        <p>Cybersecurity Enthusiast | IT Professional</p>
      </header>

      <FadeInSection>
        <Portfolio /> {/* Render the Portfolio component */}
      </FadeInSection>
    </div>
  );
}

export default App;
